    <form id="course_top_search">        <div class="bar bar-header-secondary">            <div class="searchbar">                <div class="close">                    <img src="http://localhost/xiaomaolu/cust/images/close.png" alt="">                </div>                <div class="search-input">                    <label class="icon icon-search" for="search"></label>                    <input type="search" id="course_search" placeholder="请输入课程名称" value="1">                </div>                <div class="searchButton">                    <span>搜索</span>                </div>            </div>        </div>    </form>    <!-- 这里是页面内容区 -->    <div class="content searchResultBk" id="searchResultBk" style="background-color: #ffffff">        <div class="content-block">            <div class="list-block media-list"  id="searchResult">            </div>            <div class="noSearchContent">                <span>暂无搜索内容，试试其他关键词吧~</span>            </div>        </div>    </div></div><script type='text/javascript' src='cust/js/sui/js/zepto.js' charset='utf-8'></script><script type='text/javascript' src='cust/js/sui/js/sm.min.js' charset='utf-8'></script><script type='text/javascript' src='cust/js/sui/js/sm-extend.min.js' charset='utf-8'></script><script src="public/append/layer/layer/mobile/layer.js" ></script>    <script type="text/javascript">        // var close=document.querySelector('.close')        // close.addEventListener('click',function () {        //     window.location.href='home/index'        // })        function search() {            if($('#course_search').val()){                var url = "Home/Course/searchCourse/"+encodeURIComponent($('#course_search').val());                var searchResult=document.querySelector('#searchResult')                var noSearchContent=document.querySelector('.noSearchContent')                $.getJSON(url,function (data) {                    if(data && data.length > 0){                        var ret = data;                        var html = "";                        for(var i=0;i<ret.length;i++){                            html += '<a href="Home/Course/buy/'+ret[i].id+'"><ul><li><div class="item-content">';                            html += '<div class="item-media"><img src="'+ret[i].article_image+'" style="width: 6rem;"></div>';                            html += ' <div class="item-inner"> <div class="item-title-row"><div class="item-title" style="white-space: normal">'+ret[i].title+'</div></div>';                            html += ' <div style="padding-top:1rem">￥'+ret[i].price+'</div><div  style="text-decoration:line-through">￥'+ret[i].old_price+'</div>';                            html += '</div></div></li></ul></a>';                        }                        $('#searchResult').empty();                        $('#searchResultBk').removeClass('searchResultBk');                        $('#searchResult').append(html);                        noSearchContent.setAttribute('style','display:none')                    }else{                        $('#searchResult').empty();                        noSearchContent.removeAttribute('style')                        $('#searchResultBk').addClass('searchResultBk');                    }                })            }        }        $(function () {            $('.searchButton').click(function () {                search();            $('#course_top_search ').submit(function () {                search();                return false;            });            if($('#course_search').val()){                search();            }        });    </script></body></html>