="text/javascript">            var ishot = 0;            var page = 1;            function setHot(obj) {                if($(obj).hasClass("radius-action")){                    $(obj).removeClass("radius-action");                    ishot = 0;                }else{                    $(obj).addClass("radius-action");                    ishot = 1;                }                page = 1;                $('#mainContent').empty();                search()            }            function search() {                    var url = "Home/Course/getTypeListDatas/<?=$course_type?>/"+ishot+"/"+page;                    $.showPreloader();                    $.getJSON(url,function (data) {                         var ret = data.ret;                        if(ret && ret.length > 0){                            for(var i=0;i<ret.length;i++){                                var sb='<div class="content-block" style="margin: 0.5rem 0">';                                sb+='                <div class="row" style="border-bottom: 1px solid #d2d2d2;margin-left: unset">';                                sb+='                    <div class="col-100 kc_list" style="width: 100%;height:125px;margin-left: unset">';                                sb+='                        <img style="width: 100%;height: 100%" src="'+ret[i].article_image+'" />';                                sb+='                    </div>';                                sb+='                    <div class="col-100 kc-title" style="color: black;margin-left: unset">';                                sb+= ret[i].title;                                sb+='                    </div>';                                sb+='                    <div class="col-100 kc-price" style="margin-left: unset;width: 100%">';                                sb+='                        <div class="row  " style="margin-left: unset ">';                                sb+='                            <div class="col-20" style="color:red;font-size: 1rem;width:30%;margin-left: unset">'+ret[i].price+'</div>';                                sb+='                            <div class="col-50" style="transform:translate(-20px,0) !important;text-decoration: line-through;color: #878787;font-size: 1rem;margin-left: unset">'+ret[i].old_price+'</div>';                                sb+='                            <div class="col-20" style="width: 25% !important;position: absolute;right: 6px;"><a class="button button-round button-fill external" href="Home/Course/buy/'+ret[i].id+'" style="background: #FFBA00;color:#000">去学习</a></div>';                                sb+='                        </div>';                                sb+='                        <div class="kc-after"></div>';                                sb+='                    </div>';                                sb+='';                                sb+='                </div>';                                sb+='            </div>';                                $('#mainContent').append(sb);                            }                        }else{                            // 加载完毕，则注销无限加载事件，以防不必要的加载                            $.detachInfiniteScroll($('.infinite-scroll'));                            // 删除加载提示符                            $('.infinite-scroll-preloader').remove();                        }                    });                $.hidePreloader();            }            search();            $(document).on('infinite', '.infinite-scroll-bottom',function() {                page ++;                search();            })        </script></body></html>